import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Component } from "react";

const imgList = [
  "https://m.artish.co.kr/web/product/big/shop1_4526e01b58aad2c7dd9a041cb68cdf57.jpg",
  "https://cdn.artsnculture.com/news/thumbnail/202101/2390_6260_330_v150.jpg",
  "https://d39q7aik4bbc0b.cloudfront.net/media/artwork/image/main/CME1YAY1002_main.jpg",
  "https://d1z7ls0lpgvz0q.cloudfront.net/media/artworks/B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002%EC%9C%A0%EC%98%81%EA%B5%AD_work_kK7Mtr7_NlXR3aH_jtPht89_41oWYyV_tbjMysU_mgdjFrd_ev8a11e_7ooJyMs_6w016v9_XAJYXa3_DPgWF_GOM2hcy.png",
  "https://contents.sixshop.com/thumbnails/uploadedFiles/96396/product/image_1560847627083_750.png",
];

export class YeonSlider extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
    };
    return (
      <div>
        <h2> Single Item</h2>
        <Slider {...settings}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>
      </div>
    );
  }
}
