import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Component } from "react";
import "../App.css";

export class YeonSlider extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
    };
    return (
      <div>
        <Slider {...settings}>
          <div>
            <div className="mainSliderImg">
              <img src="https://m.artish.co.kr/web/product/big/shop1_4526e01b58aad2c7dd9a041cb68cdf57.jpg"></img>
            </div>
          </div>
          <div>
            <div className="mainSliderImg">
              <img src="https://cdn.artsnculture.com/news/thumbnail/202101/2390_6260_330_v150.jpg"></img>
            </div>
          </div>
          <div>
            <div className="mainSliderImg">
              <img src="https://d39q7aik4bbc0b.cloudfront.net/media/artwork/image/main/CME1YAY1002_main.jpg"></img>
            </div>
          </div>
          <div>
            <div className="mainSliderImg">
              <img src="https://d1z7ls0lpgvz0q.cloudfront.net/media/artworks/B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002B56YYK1002%EC%9C%A0%EC%98%81%EA%B5%AD_work_kK7Mtr7_NlXR3aH_jtPht89_41oWYyV_tbjMysU_mgdjFrd_ev8a11e_7ooJyMs_6w016v9_XAJYXa3_DPgWF_GOM2hcy.png"></img>
            </div>
          </div>
          <div>
            <div className="mainSliderImg">
              <img src="https://contents.sixshop.com/thumbnails/uploadedFiles/96396/product/image_1560847627083_750.png"></img>
            </div>
          </div>
        </Slider>
      </div>
    );
  }
}
